import React, { useState } from "react";
import { Nav, Navbar, NavItem} from "reactstrap";
import { NavLink } from "react-router-dom";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    const [customerNav, setCustomerNav] = useState(true)
    const [shippingNav, setShippingNav] = useState(false)
    const [paymentNav, setPaymentNav] = useState(false)

    const customerNavHandler = () => {
        setCustomerNav(true);
        setShippingNav(false);
        setPaymentNav(false)
    }

    const shippingNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(true);
        setPaymentNav(false)
    }

    const paymentNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(false);
        setPaymentNav(true)
    }

    const activeNavHeader={
        borderBottom:'4px solid #a5d028',
        color:"black",
        fontWeight:900,
        fontFamily: 'Mulish',
    }

    const subNavStyle = {
        color: 'black !important',
        padding:'5px',
        borderRadius:'3px',
        textDecoration:'none!important',
        fontWeight:'600',
        fontSize:'18px',
        fontFamily: 'Mulish'
    }

    return (
        <div style={{padding:'60px 10% 5% 10%'}}>
            <Nav style={{display:'flex', flexDirection:'row', justifyContent:'space-around', padding:0}}>
                <NavLink className={subNavStyle} style={{textDecoration:'none',fontSize:14, color:'#dbdbdb',padding:24, width:'100%'}} to={"#"} activeStyle={activeNavHeader} onClick={() => customerNavHandler()}>01 CUSTOMER INFO</NavLink>
                <NavLink className={subNavStyle} style={{textDecoration:'none',fontSize:14, color:'#dbdbdb',padding:24, width:'100%'}} to={"#"} activeStyle={activeNavHeader} onClick={() => shippingNavHandler()}>02 SHIPPING INFO</NavLink>
                <NavLink className={subNavStyle} style={{textDecoration:'none',fontSize:14, color:'#dbdbdb',padding:24, width:'100%'}} to={"#"} activeStyle={activeNavHeader} onClick={() => paymentNavHandler()}>03 PAYMENT SELECTION</NavLink>
            </Nav>
            {customerNav ? <CustomerInfoPage /> :  null}
            {shippingNav ? <ShippingInfoPage /> :  null}
            {paymentNav ? <PaymentInfoPage /> :  null}
        </div>
    )
}
