import React, { useState } from "react";
import { Nav, Navbar, NavItem,NavLink} from "reactstrap";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    const [customerNav, setCustomerNav] = useState(true)
    const [shippingNav, setShippingNav] = useState(false)
    const [paymentNav, setPaymentNav] = useState(false)

    const customerNavHandler = () => {
        setCustomerNav(true);
        setShippingNav(false);
        setPaymentNav(false)
    }

    const shippingNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(true);
        setPaymentNav(false)
    }

    const paymentNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(false);
        setPaymentNav(true)
    }

    const activeNavHeader={
        borderBottom:'4px solid #a5d028',
        color:"#261640",
        fontWeight:900,
        fontFamily: 'Mulish',
        // border:'1px solid black',
    }

    return (
        <div style={{padding:'60px 10% 5% 10%'}}>
            <Nav style={{display:'flex', flexDirection:'row', justifyContent:'space-around',border:'1px solid black', padding:0}}>
                <NavLink style={{cursor:'pointer', padding:25, width:'100%', textAlign:'center'}} activeStyle={activeNavHeader} onClick={() => customerNavHandler()}>01 CUSTOMER INFO</NavLink>
                <NavLink style={{cursor:'pointer', padding:25, width:'100%', textAlign:'center'}} activeStyle={activeNavHeader} onClick={() => shippingNavHandler()}>02 SHIPPING INFO</NavLink>
                <NavLink style={{cursor:'pointer', padding:25, width:'100%', textAlign:'center'}} activeStyle={activeNavHeader} onClick={() => paymentNavHandler()}>03 PAYMENT SELECTION</NavLink>
            </Nav>
            {customerNav ? <CustomerInfoPage /> :  null}
            {shippingNav ? <ShippingInfoPage /> :  null}
            {paymentNav ? <PaymentInfoPage /> :  null}
        </div>
    )
}
