import React, { useState } from "react";
import { Nav, Navbar, NavItem,NavLink} from "reactstrap";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    const [customerNav, setCustomerNav] = useState(true)
    const [shippingNav, setShippingNav] = useState(false)
    const [paymentNav, setPaymentNav] = useState(false)

    const customerNavHandler = () => {
        setCustomerNav(true);
        setShippingNav(false);
        setPaymentNav(false)
    }

    const shippingNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(true);
        setPaymentNav(false)
    }

    const paymentNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(false);
        setPaymentNav(true)
    }

    let customerNavBorder = '1px solid red'
    let shippingNavBorder = '1px solid red'
    let paymentNavBorder = '1px solid red'

    if(customerNav){
        customerNavBorder = '1px solid black'
    } else if(shippingNav){
        shippingNavBorder = '1px solid black'
    } else if(paymentNav){
        paymentNavBorder = '1px solid black'    
    }


    return (
        <div style={{padding:'60px 5% 5% 5%'}}>
            {/* <Navbar style={{display:'flex', flexDirection:'row', justifyContent:'space-around'}}> */}
                <Nav>
                    <NavItem>
                        <NavLink style={{cursor:'pointer', padding:25, width:'100%', borderBottom:{customerNavBorder}, textAlign:'center'}} onClick={() => customerNavHandler()}>01 CUSTOMER INFO</NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink style={{cursor:'pointer', padding:25, width:'100%', borderBottom:{shippingNavBorder}, textAlign:'center'}} onClick={() => shippingNavHandler()}>02 SHIPPING INFO</NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink style={{cursor:'pointer', padding:25, width:'100%', borderBottom:{paymentNavBorder}, textAlign:'center'}} onClick={() => paymentNavHandler()}>03 PAYMENT SELECTION</NavLink>
                    </NavItem>
                </Nav>
            {/* </Navbar> */}
            {customerNav ? <CustomerInfoPage /> :  null}
            {shippingNav ? <ShippingInfoPage /> :  null}
            {paymentNav ? <PaymentInfoPage /> :  null}
        </div>
    )
}
