import React, { useEffect, useState } from "react";
import {FiChevronDown} from 'react-icons/fi'

export default function CartPage(){
    const [cartItem, setCartItem] = useState([
        {
            name:'Lightweight College Backpack',
            price:99.90,
            image:'https://www.google.com/search?q=college+bags+for+men+image&tbm=isch&ved=2ahUKEwiQ2oON3vj5AhUyidgFHZnXCuQQ2-cCegQIABAA&oq=college+bags+for+men+image&gs_lcp=CgNpbWcQAzoKCAAQsQMQgwEQQzoGCAAQHhAHOgYIABAeEAg6BAgAEB46BggAEB4QBVDQAlijDmDtEmgAcAB4AIAB0QGIAfAHkgEFMC42LjGYAQCgAQGqAQtnd3Mtd2l6LWltZ8ABAQ&sclient=img&ei=e1cTY9CwIbKS4t4Pma-roA4&bih=912&biw=1600&rlz=1C5CHFA_enIN987IN987#imgrc=zACd7G908sbNXM'
        },
        {
            name:'Business Casual Leather Watch Waterproof',
            price:69.90,
            image:'https://www.google.com/search?q=watches+for+men+image&tbm=isch&ved=2ahUKEwjYvP2a3vj5AhWg5XMBHR7dAf4Q2-cCegQIABAA&oq=watches+for+men+image&gs_lcp=CgNpbWcQAzIFCAAQgAQyBggAEB4QBTIGCAAQHhAIOgYIABAeEAc6CAgAEB4QBxAFUPcJWO8bYIcdaABwAHgAgAG4AYgBsgiSAQMyLjaYAQCgAQGqAQtnd3Mtd2l6LWltZ8ABAQ&sclient=img&ei=mFcTY9iQMaDLz7sPnrqH8A8&bih=912&biw=1600&rlz=1C5CHFA_enIN987IN987#imgrc=hTRKNOT_mAKy_M'
        }
    ])

    const [subTotalPrice, setSubTotalPrice] = useState(0);
    const [shippingPrice, setShippingPrice] = useState(0);
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {

        setShippingPrice(30);

        let subTotalPrice = 0;
        cartItem.map((item) => {
            return subTotalPrice += item.price
        })

        setSubTotalPrice(subTotalPrice);

        let totalPrice = 0;

        if(shippingPrice != 0){
            totalPrice = subTotalPrice+shippingPrice
            setTotalPrice(totalPrice);
        } else {
            totalPrice = subTotalPrice
            setTotalPrice(totalPrice);
        }


        console.log(subTotalPrice, shippingPrice, totalPrice);

    },[shippingPrice])

    const displayItem = cartItem.map((item, i) => {
        return (
            <div key={i}>
                <div>
                    <img src={item.image} />
                </div>
                <div>
                    <p>{item.name}</p>
                    <p>{item.price}</p>
                </div>
            </div>
        )
    })

    return (
        <div>
            <div>
                <span>Shopping Cart</span>
                <span>{cartItem.length}</span>
            </div>
            <hr />
            <div>
                {displayItem}
            </div>
            <hr />
            <div>
                <span>Have a Voucher?</span>
                <span><FiChevronDown /></span>
            </div>
            <hr />
            <div>
                <div>
                    <span>Subtotal</span>
                    <span>{subTotalPrice.toFixed(2)}</span>
                </div>
                <div>
                    <span>Shipping</span>
                    <span>{shippingPrice !== 0 ? shippingPrice : 'Free'}</span>
                </div>
            </div>
            <hr />
            <div>
                <span>Total</span>
                <span>{totalPrice.toFixed(2)}</span>
            </div>

        </div>
    )
}
