import React, { useState } from "react";
import { Modal, ModalBody, Nav, Navbar, NavItem} from "reactstrap";
import { NavLink } from "react-router-dom";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    const [customerNav, setCustomerNav] = useState(true)
    const [shippingNav, setShippingNav] = useState(false)
    const [paymentNav, setPaymentNav] = useState(false)


    const model = (
        <Modal isOpen={true}>
            <ModalBody>
                <text>Hi</text>
            </ModalBody>

        </Modal>
    )

    return (
        <div style={{padding:'60px 10% 5% 10%'}}>
            <div style={{display:'flex', flexDirection:'row', justifyContent:'space-around', padding:0}}>
                <div style={{textDecoration:'none',fontSize:14, color:customerNav ?'black' : '#dbdbdb',padding:24, width:'100%'}}>01 CUSTOMER INFO</div>
                <div style={{textDecoration:'none',fontSize:14, color:shippingNav ?'black' : '#dbdbdb',padding:24, width:'100%'}}>02 SHIPPING INFO</div>
                <div style={{textDecoration:'none',fontSize:14, color:paymentNav ?'black' : '#dbdbdb',padding:24, width:'100%'}}>03 PAYMENT SELECTION</div>
            </div>
            {customerNav ?  <CustomerInfoPage setCustomerNav={setCustomerNav} setShippingNav={setShippingNav} setPaymentNav={setPaymentNav}/> :  null}
            {shippingNav ?  <ShippingInfoPage setCustomerNav={setCustomerNav} setShippingNav={setShippingNav} setPaymentNav={setPaymentNav}/> :  null}
            {paymentNav  ?  <PaymentInfoPage setCustomerNav={setCustomerNav} setShippingNav={setShippingNav} setPaymentNav={setPaymentNav}/> :  null}

            {/* {!customerNav && !shippingNav && !paymentNav ? {model} : null} */}
        </div>
    )
}
