import React, { useState } from "react";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    const [customerNav, setCustomerNav] = useState(true)
    const [shippingNav, setShippingNav] = useState(false)
    const [paymentNav, setPaymentNav] = useState(false)

    const customerNavHandler = () => {
        setCustomerNav(true);
        setShippingNav(false);
        setPaymentNav(false)
    }

    const shippingNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(true);
        setPaymentNav(false)
    }

    const paymentNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(false);
        setPaymentNav(true)
    }

    const navbarItems = (
        <div>
            <span style={{cursor:'pointer'}} onClick={() => customerNavHandler()}>01 Customer Info</span>
            <span style={{cursor:'pointer'}} onClick={() => shippingNavHandler()}>02 Shipping Info</span>
            <span style={{cursor:'pointer'}} onClick={() => paymentNavHandler()}>03 Payment Selection</span>
        </div>
    )

    return (
        <div>
            {navbarItems}
            {customerNav ? <CustomerInfoPage /> :  null}
            {shippingNav ? <ShippingInfoPage /> :  null}
            {paymentNav ? <PaymentInfoPage /> :  null}
        </div>
    )
}
