import React, { useState } from "react";
import { Nav, Navbar, NavItem} from "reactstrap";
import { NavLink } from "react-router-dom";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    const [customerNav, setCustomerNav] = useState(true)
    const [shippingNav, setShippingNav] = useState(false)
    const [paymentNav, setPaymentNav] = useState(false)

    const customerNavHandler = () => {
        setCustomerNav(true);
        setShippingNav(false);
        setPaymentNav(false)
    }

    const shippingNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(true);
        setPaymentNav(false)
    }

    const paymentNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(false);
        setPaymentNav(true)
    }

    return (
        <div style={{padding:'60px 10% 5% 10%'}}>
            <div style={{display:'flex', flexDirection:'row', justifyContent:'space-around', padding:0}}>
                <div style={{textDecoration:'none',fontSize:14, color:customerNav ?'black' : '#dbdbdb',padding:24, width:'100%'}} onClick={() => customerNavHandler()}>01 CUSTOMER INFO</div>
                <div style={{textDecoration:'none',fontSize:14, color:shippingNav ?'black' : '#dbdbdb',padding:24, width:'100%'}} onClick={() => shippingNavHandler()}>02 SHIPPING INFO</div>
                <div style={{textDecoration:'none',fontSize:14, color:paymentNav ?'black' : '#dbdbdb',padding:24, width:'100%'}} onClick={() => paymentNavHandler()}>03 PAYMENT SELECTION</div>
            </div>
            {customerNav ? <CustomerInfoPage /> :  null}
            {shippingNav ? <ShippingInfoPage /> :  null}
            {paymentNav ? <PaymentInfoPage /> :  null}
        </div>
    )
}
