import React, { useState } from "react";
import CustomerInfoPage from "./CustomerInfo";
import PaymentInfoPage from "./PaymentInfo";
import ShippingInfoPage from "./ShippingInfo";

export default function CheckoutPage(props){

    let nonActiveNav = {
        cursor:'pointer',
        padding:25,
        borderBottom:'1px solid grey'

    }

    let activeNav = {
        cursor:'pointer',
        padding:25,
        borderBottom:'1px solid black'

    }

    const [customerNav, setCustomerNav] = useState(true);
    const [shippingNav, setShippingNav] = useState(false);
    const [paymentNav, setPaymentNav] = useState(false);
    const [activeNavItem, setActiveNavItem] = useState(nonActiveNav)




    const customerNavHandler = () => {
        setCustomerNav(true);
        setShippingNav(false);
        setPaymentNav(false)
    }

    const shippingNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(true);
        setPaymentNav(false)
    }

    const paymentNavHandler = () => {
        setCustomerNav(false);
        setShippingNav(false);
        setPaymentNav(true)
    }



    const navbarItems = (
        <div style={{display:'flex', flexDirection:'row', justifyContent:'space-around', border:'1px solid black'}}>
            <span className={activeNavItem} onClick={() => customerNavHandler()}>01 Customer Info</span>
            <span className={activeNavItem} onClick={() => shippingNavHandler()}>02 Shipping Info</span>
            <span className={activeNavItem} onClick={() => paymentNavHandler()}>03 Payment Selection</span>
        </div>
    )



    return (
        <div style={{padding:'10%'}}>
            {navbarItems}
            {customerNav ? <CustomerInfoPage /> :  null}
            {shippingNav ? <ShippingInfoPage /> :  null}
            {paymentNav ? <PaymentInfoPage /> :  null}
        </div>
    )
}
